import React from 'react';
import './App.css';
import pokemons from './data';
import Pokemon from './Pokemon';

class App extends React.Component {
  render() {
    return (
      <div className="App">
        {
          pokemons.map((elemento) => <Pokemon
            key={elemento.id} 
            name={elemento.name}
            type={elemento.name}
            averageWeight={elemento.averageWeight}
            image={elemento.image}
            />
          )
        }
      </div>
    )
  }
}

export default App;
